// Local placeholder for PDF.js to avoid CDN dependency.
// Replace with full pdf.min.js build if you need full fidelity rendering.
(function(){
  if (window.pdfjsLib) return;
  const stubError = new Error('PDF.js not bundled in this environment');
  const rejected = Promise.reject(stubError);
  rejected.catch(() => {});
  window.pdfjsLib = {
    getDocument: () => ({ promise: rejected }),
    GlobalWorkerOptions: {}
  };
})();
