// Placeholder worker for PDF.js when the full worker build is not bundled locally.
// The main pdf.min.js stub does not offload work, but keeping this file prevents 404s.
self.addEventListener('message', function(event) {
  // No-op worker to satisfy PDF.js workerSrc references.
  self.postMessage({ type: 'pdfjs-placeholder', id: event.data && event.data.id });
});
